package tests;import kong.unirest.Unirest;import org.junit.jupiter.api.Test;import kong.unirest.HttpResponse;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertTrue;public class APITest {        @Test    public void shouldReturnStatus200ForGetRequest() {        HttpResponse<String> response = Unirest.get("https://jsonplaceholder.typicode.com/todos/1").asString();        assertEquals(200, response.getStatus());        assertTrue(response.getBody().contains("userId"));    }}