package tests;import com.microsoft.playwright.*;import org.junit.jupiter.api.AfterAll;import org.junit.jupiter.api.AfterEach;import org.junit.jupiter.api.BeforeAll;import org.junit.jupiter.api.BeforeEach;public class BaseTest {    protected static Playwright playwright;    protected static Browser browser;    protected BrowserContext context;    protected Page page;        @BeforeAll    static void setupClass() {        playwright = Playwright.create();        browser = playwright.chromium().launch(new BrowserType.LaunchOptions().setHeadless(false));    }        @BeforeEach    void setupTest() {        context = browser.newContext();        page = context.newPage();    }        @AfterEach    void tearDown() {        context.close();    }        @AfterAll    static void tearDownClass() {        browser.close();        playwright.close();    }}